mapper.select:
select * from review_view 
where category = #{category}

	and type = #{type}

order by review_no desc

select count(*) from review_view 
where category = #{category}

	and type = #{type}

mapper.update:
update review set review_title = #{revTitle}, review_region = #{revRegion},
review_image = #{revImage}, review_content = #{revContent}, 
review_prod = #{revProd}, review_price = #{revPrice}, category = #{category}
where review_no = #{reviewNo}
